package patternPrograms;

public class Rectangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b = 4;
		int l = 8;
		for (int i = 1; i <= b; i++) {
			for (int j = 1; j <= l; j++) {
				System.out.print("* ");
			}
			System.out.println();
		}
	}

}
