package switchCases;

public class switchCase2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
char c='+';
int a=1,b=2;
switch(c){
case '+':
	System.out.println(a+b);
	break;
case '-':
	System.out.println(a-b);
	break;
case '*':
	System.out.println(a*b);
	break;
case '/':
	System.out.println(a/b);
	break;
default:
	System.out.println("Invalid mark");
}}

}
